axes(-5,5,-6,6);
e(3,5,0,0);
dot((0,4)^^(0,-4)^^(0,5)^^(0,-5));
yh(4,3,0,0,-3,3);
dot((9/sqrt(41),20*sqrt(2)/sqrt(41)));
[/asy]
